import React, {component, Component} from "react";
import './Mahasiswa.css';
import PostMahasiswa from '../../component/BlogPost/PostMahasiswa';

class Mahasiswa extends Component{
    state={
        listMahasiswa: [],
        insertMahasiswa: {
            id: 1,
            nim: "",
            nama: "",
            alamat: "",
            hp: "",
            angkatan: "",
            status: ""
        }
    }

    ambilDataDariServerAPI = () =>{
        fetch('http://localhost:3001/Mahasiswa?_sort=id&_order=desc')
        .then(response => response.json())
        .then(jsonHasilAmbilDariAPI => {
            this.setState({
                listMahasiswa: jsonHasilAmbilDariAPI
             })
        })
    }

    componentDidMount(){
        this.ambilDataDariServerAPI()
    }

    handlehapusMahasiswa = (data) => {
        fetch(`http://localhost:3001/Mahasiswa/${data}`, {method: 'DELETE'})
        .then(res => {
            this.ambilDataDariServerAPI()
        })
    }

    handletambahMahasiswa = (event) => {
        let formInsertMahasiswa = {...this.state.insertMahasiswa};
        let timestamp = new Date().getTime();
        formInsertMahasiswa['id'] = timestamp;
        formInsertMahasiswa[event.target.name] = event.target.value;
        this.setState ({
            insertMahasiswa: formInsertMahasiswa
        });
    }

    handletombolSimpan = () => {
        fetch('http://localhost:3001/Mahasiswa', {
            method : 'post',
            headers: {
                'Accept' : 'application/json',
                'Content-Type' : 'application/json'
            },
            body: JSON.stringify(this.state.insertMahasiswa)
        })
        .then(( Response) => {
            this.ambilDataDariServerAPI();
        });
    }

    render(){
        return(
            <div className="post-Mahasiswa">
                <div className="form pb-2 border-bottom">
                    <div className="from-group row">
                        <label htmlFor="nim" className="col-sm-2 col-form-label">Judul</label>
                        <div className="col-sm-10">
                            <input type="text" className="form-control" id="nim"
                            name="nim" onChange={this.handletambahMahasiswa}/>
                        </div>
                    </div>
                    <div className="form-group row">
                    <label htmlFor="nama" className="col-sm-2 col-form-label">Isi</label>
                        <div className="col-sm-10">
                            <input type="text" className="form-control" id="nama" name="nama"
                            onChange={this.handletambahMahasiswa}></input>
                        </div>
                    </div>
                    <div className="form-group row">
                    <label htmlFor="alamat" className="col-sm-2 col-form-label">Isi</label>
                        <div className="col-sm-10">
                            <input type="text" className="form-control" id="alamat" name="alamat"
                            onChange={this.handletambahMahasiswa}></input>
                        </div>
                    </div>
                    <div className="form-group row">
                    <label htmlFor="hp" className="col-sm-2 col-form-label">Isi</label>
                        <div className="col-sm-10">
                            <input type="text" className="form-control" id="hp" name="hp"
                            onChange={this.handletambahMahasiswa}></input>
                        </div>
                    </div>
                    <div className="form-group row">
                    <label htmlFor="angkatan" className="col-sm-2 col-form-label">Isi</label>
                        <div className="col-sm-10">
                            <input type="text" className="form-control" id="angkatan" name="angkatan"
                            onChange={this.handletambahMahasiswa}></input>
                        </div>
                    </div>
                    <div className="form-group row">
                    <label htmlFor="status" className="col-sm-2 col-form-label">Isi</label>
                        <div className="col-sm-10">
                            <input type="text" className="form-control" id="status" name="status"
                            onChange={this.handletambahMahasiswa}></input>
                        </div>
                    </div>
                    <button type="submit" className="btn btn-primary" onClick={this.handletombolSimpan}>Simpan</button>
                </div>
                <h2>Daftar Mahasiswa</h2>
                {
                    this.state.listMahasiswa.map (Mahasiswa => {
                        return <PostMahasiswa key={Mahasiswa.id} nim={Mahasiswa.nim} nama={Mahasiswa.nama} alamat={Mahasiswa.alamat} hp={Mahasiswa.hp} angkatan={Mahasiswa.angkatan} status={Mahasiswa.status}
                        idMahasiswa={Mahasiswa.id} hapusMahasiswa={this.handlehapusMahasiswa} />
                    })
                }
            </div>
        )
    }
}

export default BlogPost;
