import React, { createRef, createContext } from "react";

const Post = (props) => {
    return (
        <div className="artikel">
            <div className="gambar-artikel">
                <img src="http://placeimg.com/120/120/any" alt="Gambar Tumbnail Artikel"/>
            </div>
            <div class="konten-artikel">
                <div class="judul-artikel">{props.judul}</div>
                <p class="isi-artikel">{props.isi}</p>
            </div>
        </div>
    )
}

export default Post;
